const TravelBucketlist = () => {
  const [formValues, setFormValues] = useState({
    cities: []
  });
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const [token, setToken] = useState(null);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    setToken(localStorage.getItem("token"))
    setUserId(localStorage.getItem("userId"))
    setFormValues((prevValues) => ({
      ...prevValues,
    }));
  }, []);

  const handleCityChange = (event) => {
    const { value } = event.target;
    setFormValues((prevValues) => ({
      ...prevValues,
      cities: value.split(","),
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    setIsLoading(true);

    try {
      const profileData = {
        cities: formValues.cities,
      };

      await userProfile(userId, token, profileData);

      toast.success("Profile added", { autoClose: 2000 });
      navigate("/finalstep"); // Redirect 
    } catch (error) {
      toast.error("Error creating profile", { autoClose: 2000 });
    }

    setIsLoading(false);
  };

  return (
    <div className="user-info layout-container">
      <form>
        <div className="input-validation">
          <label htmlFor="cities">Travel Bucketlist Cities:</label>
          <input
            type="text"
            name="cities"
            id="cities"
            value={formValues.cities.join(",")}
            onChange={handleCityChange}
            required
          />
        </div>

        <button
          className="submit-button"
          onClick={handleSubmit}
          disabled={isLoading}
        >
          {isLoading ? <LoadingSpinner /> : <FontAwesomeIcon icon={faAngleRight} />}
        </button>
      </form>
      <ToastContainer />
    </div>
  );
};
