import React, { useEffect, useState } from "react";
import { userProfile } from "../../api";
import "../../assets/Style/addprofile.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import LoadingSpinner from "../loading_spinner";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngleRight } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from "react-router-dom";

const accordionData = {
  "health&fitness": "Health & Fitness",
  "sportsAndCreation": "Sports and Creation",
  "creative&Performance": "Creative & Performance",
  "specialInterestHousing": "Special Interest Housing",
  "educationAndStudentLife": "Education and Student Life",
  "lifestyle&entertainment": "Lifestyle & Entertainment",
  "buisnessAndEnterprenuership": "Business and Entrepreneurship"
};

const optionData = {
  "health&fitness": ["Option 1", "Option 2", "Option 3", "Option 4", "Option 5"],
  "sportsAndCreation": ["Option 1", "Option 2", "Option 3", "Option 4", "Option 5"],
  "creative&Performance": ["Option 1", "Option 2", "Option 3", "Option 4", "Option 5"],
  "specialInterestHousing": ["Option 1", "Option 2", "Option 3", "Option 4", "Option 5"],
  "educationAndStudentLife": ["Option 1", "Option 2", "Option 3", "Option 4", "Option 5"],
  "lifestyle&entertainment": ["Option 1", "Option 2", "Option 3", "Option 4", "Option 5"],
  "buisnessAndEnterprenuership": ["Option 1", "Option 2", "Option 3", "Option 4", "Option 5"]
};

const InterestMatching = () => {
  const [formValues, setFormValues] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const [token, setToken] = useState(null);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    setToken(localStorage.getItem("token"))
    setUserId(localStorage.getItem("userId"))
    setFormValues((prevValues) => ({
      ...prevValues,
    }));
  }, []);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormValues((prevValues) => ({
      ...prevValues,
      [name]: value,
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const invalidAccordions = Object.values(formValues).some((value) => value === "");
    if (invalidAccordions) {
      toast.error("Please fill in all the accordions", { autoClose: 2000 });
      return;
    }

    setIsLoading(true);

    try {
      const profileData = formValues;

      await userProfile(userId, token, profileData);

      toast.success("Profile added", { autoClose: 2000 });
      navigate("/interestmatching"); // Redirect to "/interestmatching"
    } catch (error) {
      toast.error("Error creating profile", { autoClose: 2000 });
    }

    setIsLoading(false);
  };

  return (
    <div className="user-info layout-container">
      <form>
        {Object.entries(accordionData).map(([key, title]) => (
          <div className="accordion" key={key}>
            <h2>{title}</h2>
            <select
              name={key}
              value={formValues[key] || ""}
              onChange={handleChange}
              required
            >
              <option value="">Select option</option>
              {optionData[key].map((option) => (
                <option key={option} value={option}>
                  {option}
                </option>
              ))}
            </select>
          </div>
        ))}

        <button
          className="submit-button"
          onClick={handleSubmit}
          disabled={isLoading}
        >
          {isLoading ? <LoadingSpinner /> : <FontAwesomeIcon icon={faAngleRight} />}
        </button>
      </form>
      <ToastContainer />
    </div>
  );
};

export default InterestMatching;
