import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus, faAngleRight } from "@fortawesome/free-solid-svg-icons";
import { useNavigate } from "react-router-dom";
import { userProfile } from "../../api";
import "../../assets/Style/travelbucket.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import LoadingSpinner from "../loading_spinner";

const TravelBucket = () => {
  const [cities, setCities] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const [token, setToken] = useState(null);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    setToken(localStorage.getItem("token"));
    setUserId(localStorage.getItem("userId"));
    setCities([]);
  }, []);

  const handleCityChange = (event) => {
    const { value } = event.target;
    setCities(value.split(","));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (cities.length === 0) {
      toast.error("Please enter at least one city", { autoClose: 2000 });
      return;
    }

    setIsLoading(true);

    try {
      const profileData = {
        cities: JSON.stringify(cities),
      };

      await userProfile(userId, token, profileData);

      toast.success("Profile added", { autoClose: 2000 });
      navigate("/profile"); // Redirect to "/profile"
    } catch (error) {
      toast.error("Error creating profile", { autoClose: 2000 });
    }

    setIsLoading(false);
  };

  const handleAddCity = () => {
    setCities([...cities, ""]);
  };

  const handleCityDelete = (index) => {
    const updatedCities = cities.filter((city, i) => i !== index);
    setCities(updatedCities);
  };

  const handleCityInputChange = (event, index) => {
    const { value } = event.target;
    const updatedCities = [...cities];
    updatedCities[index] = value;
    setCities(updatedCities);
  };



  return (
    <div className="travel-bucketlist layout-container">
      <div className="map">
        {/* Display your map or map component here */}
        {/* You can use an external map library or API */}
        {/* For example, using an iframe with Google Maps */}
        <iframe
          title="Map"
          src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d102311.01822577446!2d-74.0059413!3d40.7127837!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x89c24fa5d33f083b%3A0xc80b8f06e177fe62!2sNew%20York%2C%20NY%2C%20USA!5e0!3m2!1sen!2sin!4v1613044534545!5m2!1sen!2sin"
          width="250"
          height="150"
          style={{ border: 0, borderRadius: "20px" }}
          allowFullScreen=""
          loading="lazy"
        ></iframe>
      </div>
      <div className="travel-info">
        <div className="city-list">
          {cityList.map((city, index) => (
            <div key={index}>{city}</div>
          ))}
        </div>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Enter city name"
            value={selectedCity}
            onChange={(e) => setSelectedCity(e.target.value)}
          />
          <FontAwesomeIcon
            icon={faPlus}
            onClick={handleAddCity}
            className="search-icon"
          />
        </div>

        <button
          className="submit-button"
          onClick={handleSubmit}
          disabled={isLoading}
        >
          {isLoading ? (
            <LoadingSpinner />
          ) : (
            <FontAwesomeIcon icon={faAngleRight} />
          )}
        </button>
      </div>
      <ToastContainer />
    </div>
  );
};

export default TravelBucketlist;
