import axios from "axios";
const BASE_URL = "http://192.168.100.8:3001";

export const postLogin = async (username, password) => {
  try {
    const response = await axios.post(`${BASE_URL}/login`, { username, password });
    console.log(response.data.token);
    const token = response.data.token;
    localStorage.setItem("token", token);
    const userId = response.data.id;
    console.log(userId);
    localStorage.setItem("userId", userId);
    console.log(localStorage.getItem("userId"));
    return response.data;
  } catch (error) {
    console.error(error);
    throw error;
  }
};
export const userProfile = async (userId, token, profileData) => {
  try {
    const requestData = {
      name: profileData.name,
      email: profileData.email,
      gender: profileData.gender,
      birthday: profileData.birthday,
      current_city: profileData.current_city,
      photos: profileData.photos,
      known_languages: profileData.known_languages,
      interest: {
        "health&fitness": profileData["health&fitness"],
        "sportsAndCreation": profileData["sportsAndCreation"],
        "creative&Performance": profileData["creative&Performance"],
        "specialInterestHousing": profileData["specialInterestHousing"],
        "educationAndStudentLife": profileData["educationAndStudentLife"],
        "lifestyle&entertainment": profileData["lifestyle&entertainment"],
        "buisnessAndEnterprenuership": profileData["buisnessAndEnterprenuership"]
      }
    };

    const response = await axios.put(`${BASE_URL}/userProfile/${userId}`, requestData, {
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      }
    });

    console.log(response.data);
    return response.data;
  } catch (error) {
    console.error(error);
    return null;
  }
};
