import React, { useEffect, useState } from "react";
import { userProfile } from "../../api";
import "../../assets/Style/addprofile.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import LoadingSpinner from "../loading_spinner";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngleRight } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from "react-router-dom";

const InterestMatching = () => {
  const [formValues, setFormValues] = useState({
    accordion1: "",
    accordion2: "",
    accordion3: "",
    accordion4: "",
    accordion5: "",
    accordion6: "",
    accordion7: "",
  });
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const [token, setToken] = useState(null);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    setToken(localStorage.getItem("token"))
    setUserId(localStorage.getItem("userId"))
    setFormValues((prevValues) => ({
      ...prevValues,
    }));
  }, []);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormValues((prevValues) => ({
      ...prevValues,
      [name]: value,
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const {
      accordion1,
      accordion2,
      accordion3,
      accordion4,
      accordion5,
      accordion6,
      accordion7,
    } = formValues;

    const isInvalidAccordion1 = accordion1 === "";
    const isInvalidAccordion2 = accordion2 === "";
    const isInvalidAccordion3 = accordion3 === "";
    const isInvalidAccordion4 = accordion4 === "";
    const isInvalidAccordion5 = accordion5 === "";
    const isInvalidAccordion6 = accordion6 === "";
    const isInvalidAccordion7 = accordion7 === "";

    if (isInvalidAccordion1 || isInvalidAccordion2 || isInvalidAccordion3 ||
        isInvalidAccordion4 || isInvalidAccordion5 || isInvalidAccordion6 || isInvalidAccordion7) {
      toast.error("Please fill in all the accordions", { autoClose: 2000 });
      return;
    }

    setIsLoading(true);

    try {
      const profileData = {
        accordion1,
        accordion2,
        accordion3,
        accordion4,
        accordion5,
        accordion6,
        accordion7,
      };

      await userProfile(userId, token, profileData);

      toast.success("Profile added", { autoClose: 2000 });
      navigate("/interestmatching"); // Redirect to "/interestmatching"
    } catch (error) {
      toast.error("Error creating profile", { autoClose: 2000 });
    }

    setIsLoading(false);
  };

  return (
    <div className="user-info layout-container">
      <form>
        <div className="accordion">
          <h2>Accordion 1</h2>
          <select
            name="accordion1"
            value={formValues.accordion1}
            onChange={handleChange}
            required
          >
            <option value="">Select option</option>
            <option value="Option 1">Option 1</option>
            <option value="Option 2">Option 2</option>
            <option value="Option 3">Option 3</option>
            <option value="Option 4">Option 4</option>
          </select>
        </div>

        {/* Repeat the above code for the remaining accordions */}

        <button
          className="submit-button"
          onClick={handleSubmit}
          disabled={isLoading}
        >
          {isLoading ? <LoadingSpinner /> : <FontAwesomeIcon icon={faAngleRight} />}
        </button>
      </form>
      <ToastContainer />
    </div>
  );
};

export default InterestMatching;
